import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Button,
  Table,
  Thead,
  Tbody,
  Tr,
  Th,
  Td,
  Select,
  Input,
  HStack,
  Text,
  Flex,
  useToast,
  Spinner,
  Center,
  SimpleGrid,
  FormControl,
  FormLabel,
} from '@chakra-ui/react';
import { DownloadIcon } from '@chakra-ui/icons';
import { getTickets, exportTickets } from '../api/ticketApi';
import { Ticket } from '../types/ticket';

const TicketDashboard: React.FC = () => {
  const [tickets, setTickets] = useState<Ticket[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [reason, setReason] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const toast = useToast();

  const fetchTickets = useCallback(async () => {
    setIsLoading(true);
    try {
      const response = await getTickets(currentPage, reason, startDate, endDate);
      setTickets(response.tickets);
      setTotalPages(response.totalPages);
    } catch (error) {
      console.error('Error fetching tickets:', error);
      toast({
        title: 'Error',
        description: 'Failed to fetch tickets',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setIsLoading(false);
    }
  }, [currentPage, reason, startDate, endDate, toast]);

  useEffect(() => {
    fetchTickets();
  }, [currentPage, reason, startDate, endDate, fetchTickets]);

  const handleExport = async () => {
    setIsExporting(true);
    try {
      await exportTickets(reason, startDate, endDate);
      toast({
        title: 'Success',
        description: 'Tickets exported successfully',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      console.error('Error exporting tickets:', error);
      toast({
        title: 'Error',
        description: 'Failed to export tickets',
        status: 'error',
        duration: 3000,
        isClosable: true,
      });
    } finally {
      setIsExporting(false);
    }
  };

  return (
    <Box p={6}>
      <Box mb={8}>
        <Text fontSize="xl" fontWeight="semibold" color="gray.700" mb={4}>
          Ticket Dashboard
        </Text>
        <SimpleGrid columns={{ base: 1, md: 4 }} spacing={4}>
          <FormControl>
            <FormLabel fontSize="sm">Filter by Reason</FormLabel>
            <Select
              value={reason}
              onChange={(e) => setReason(e.target.value)}
              bg="white"
              size="md"
              placeholder="All Reasons"
            >
              <option value="Harassment">Harassment</option>
              <option value="Drop">Drop</option>
              <option value="Bad behavior">Bad behavior</option>
              <option value="Took extra money">Took extra money</option>
            </Select>
          </FormControl>

          <FormControl>
            <FormLabel fontSize="sm">Start Date</FormLabel>
            <Input
              type="date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
              bg="white"
              size="md"
            />
          </FormControl>

          <FormControl>
            <FormLabel fontSize="sm">End Date</FormLabel>
            <Input
              type="date"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
              bg="white"
              size="md"
            />
          </FormControl>

          <Box alignSelf="flex-end">
            <Button
              onClick={handleExport}
              colorScheme="green"
              isLoading={isExporting}
              loadingText="Exporting..."
              width="full"
              leftIcon={<DownloadIcon />}
              size="md"
              boxShadow="sm"
              _hover={{ transform: 'translateY(-1px)', boxShadow: 'md' }}
            >
              Export CSV
            </Button>
          </Box>
        </SimpleGrid>
      </Box>

      {isLoading ? (
        <Center p={8}>
          <Spinner size="xl" />
        </Center>
      ) : (
        <>
          <Table variant="simple">
            <Thead>
              <Tr>
                <Th>Trip ID</Th>
                <Th>Trip Date</Th>
                <Th>Driver ID</Th>
                <Th>Reason</Th>
                <Th>City</Th>
                <Th>Service Type</Th>
                <Th>Customer Phone</Th>
                <Th>Agent Name</Th>
              </Tr>
            </Thead>
            <Tbody>
              {tickets.map((ticket) => (
                <Tr key={ticket.id}>
                  <Td>{ticket.tripId}</Td>
                  <Td>{new Date(ticket.tripDate).toLocaleDateString()}</Td>
                  <Td>{ticket.driverId}</Td>
                  <Td>{ticket.reason}</Td>
                  <Td>{ticket.city}</Td>
                  <Td>{ticket.serviceType}</Td>
                  <Td>{ticket.customerPhone}</Td>
                  <Td>{ticket.agentName}</Td>
                </Tr>
              ))}
            </Tbody>
          </Table>
          
          <Flex justify="space-between" align="center" mt={4}>
            <Text>
              Page {currentPage} of {totalPages}
            </Text>
            <HStack spacing={2}>
              <Button
                onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                isDisabled={currentPage === 1 || isLoading}
              >
                Previous
              </Button>
              <Button
                onClick={() => setCurrentPage((prev) => prev + 1)}
                isDisabled={currentPage === totalPages || isLoading}
              >
                Next
              </Button>
            </HStack>
          </Flex>
        </>
      )}

    </Box>
  );
};

export default TicketDashboard;
